		vector <vector <bool> > visited (12, vector <bool> (12, false ) );
		int res = 0;

		for (i = 0; i < 12; ++i){
			for (j = 0; j < 12; ++j){
				if (!visited[i][j] && island[i][j] == '1'){
					dfs (i, j, island, visited );
					++res;
				} // end if
			} // end for
		} // end for

		cout << res << endl;	

	} // end loop

	return 0;
}

