int dy[4] = {0, -1, 1, 0};

void bfs(int y, int x) {
	queue<P> que;
	que.push(MP(y, x));
	F[y][x] = true;

	while(!que.empty()) {
		P p = que.front();
		que.pop();

		REP(k,4) {
			int xx = p.second + dx[k];
			int yy = p.first + dy[k];
			if(xx >= 0 && yy >= 0 && xx < 12 && yy < 12) {
				if(!F[yy][xx] && M[yy][xx] == 1) {
					F[yy][xx] = true;
					que.push(MP(yy, xx));
				}
			}
		}
	}
}

int solve() {
	memset(F, 0, sizeof(F));
	int cnt = 0;
	REP(i,12) {
		REP(j,12) {
			if(M[i][j] == 1 && !F[i][j]) {
				cnt++;
				bfs(i, j);
			}
		}
	}
	return cnt;
}

