using namespace std;

char island[12][12];

void wn(int i, int j){
	island[i][j] = '0';
	for(int k = -1; k <= 1; k+=2){
		if(i + k < 0 || j + k < 0)	continue;
		if(island[i+k][j] == '1'){
			wn(i+k, j);
		}
		if(island[i][j+k] == '1'){
			wn(i, j+k);
		}
	}

	return;	
}

int main(){
	int num;

	for(int c = 0; c < 20; c++){
		num = 0;
		for(int i = 0; i < 12; i++){
			scanf("%s", island[i]);
		}

		for(int i = 0; i < 12; i++){
			for(int j = 0; j < 12; j++){
				if(island[i][j] == '1'){
					wn(i, j);
					num++;		
				}
			}
		}

		cout << num << endl << endl;
	}
	return 0;
}

#include<stdio.h>
#include<string.h>
#include<math.h>

typedef struct{
	int color;
	int number;
}Kind;

Kind U[1000000];

int main(){
	int i,j,a,c,b;

	while(~scanf("%d",&a)){
		if(a==0)break;

		memset(U,0,sizeof(U));
		b=a;
		for(i=0;i<a;i++){
			scanf("%d",&c);
			for(j=0;j<b;j++){
				if(U[j].color==c || U[j].color==0){
					U[j].color=U[j].color>0?U[j].color:c;
					++U[j].number;
					break;
				}
			}
			b=j+1;
		}

		j=-1;
		for(i=0;i<a;i++){
			if(2*U[i].number>a){
				j=i;
				break;
			}
		}

		if(j==-1){
			printf("NO COLOR\n");
		}else{
			printf("%d\n",U[j].color);
		}
	}
	return 0;
}

