int island[12][12] = {0};

void dfs(int i, int j) {
    island[i][j] = 0;

    if (i > 0 && island[i - 1][j]) {
        dfs(i - 1, j);
    }

    if (i < 11 && island[i + 1][j]) {
        dfs(i + 1, j);
    }
    if (j < 11 && island[i][j + 1]) {
        dfs(i, j + 1);
    }
    if (j > 0 && island[i][j - 1]) {
        dfs(i, j - 1);
    }

}

