int a[12][12] = { 0 };
void remove(int y, int x) {
	a[y][x] = 0;
	if (y + 1 < 12 && a[y + 1][x] == 1) remove(y + 1, x);
	if (y - 1 >= 0 && a[y - 1][x] == 1) remove(y - 1, x);
	if (x + 1 < 12 && a[y][x + 1] == 1) remove(y, x + 1);
	if (x - 1 >= 0 && a[y][x - 1] == 1) remove(y, x - 1);
}

int main() {
	string s;
	do {
		for (int i = 0; i < 12; i++) {
			cin >> s;
			for (int j = 0; j < 12; j++) {
				a[i][j] = s[j] - '0';
			}
		}

		int count = 0;
		for (int i = 0; i < 12; i++) {
			for (int j = 0; j < 12; j++) {
				if (a[i][j] == 1) {
					remove(i, j);
					count++;
				}
			}
		}
		cout << count << endl;
	} while (getline(cin, s));
	return 0;
}

