	if (i<0 || i>=12 || j<0 || j>=12) return;
	if (board[i][j]=='0') return;

	board[i][j]='0';

	island(board, i + 1, j);
	island(board, i, j + 1);
	island(board, i - 1, j);
	island(board, i, j - 1);
}

