queue<int>Q;
int z[100][100], x[100][100], sum; char a;

void BFS(int col,int x1,int y1) {
	Q.push(x1 * 100 + y1);
	int a, b;
	while (!Q.empty()) {
		a = Q.front() / 100;
		b = Q.front() % 100;
		if (z[a + 1][b] == 0 && x[a + 1][b] == 0) {
			Q.push(Q.front() + 100);
			x[a + 1][b] = col;
		}
		if (z[a - 1][b] == 0 && x[a - 1][b] == 0) {
			Q.push(Q.front() - 100);
			x[a - 1][b] = col;
		}
		if (z[a][b + 1] == 0 && x[a][b + 1] == 0) {
			Q.push(Q.front() + 1);
			x[a][b + 1] = col;
		}
		if (z[a][b - 1] == 0 && x[a][b - 1] == 0) {
			Q.push(Q.front() + 1);
			x[a][b - 1] = col;
		}
		Q.pop();
	}
}

