int a[12][12];
void solve(int m, int n) {
	if (m + 1<12 && a[m + 1][n] == 1) {
		a[m + 1][n] = 0;
		solve(m + 1, n);
	}
	if (m - 1 >= 0 && a[m - 1][n] == 1) {
		a[m - 1][n] = 0;
		solve(m - 1, n);
	}
	if (n + 1<12 && a[m][n + 1] == 1) {
		a[m][n + 1] = 0;
		solve(m, n + 1);
	}
	if (n - 1 >= 0 && a[m][n - 1] == 1) {
		a[m][n - 1] = 0;
		solve(m, n - 1);
	}
}
