			a[0][i] = (int)b[i] - '0';
		}
		for (int i = 1; i<12; i++) {
			cin >> b;
			for (int j = 0; j<12; j++) {
				a[i][j] = (int)b[j] - '0';
			}
		}
		int o = 0;
