  chk[y][x] = true;
  for(int i = 0; i < 4; ++i){
    int ny = y + vy[i];
    int nx = x + vx[i];
    if(ny >= 0 && ny < 12 && nx >= 0 && nx < 12 && field[ny][nx] && !chk[ny][nx]) dfs(ny, nx);
  }
}

