void is(int i, int j)
{
	int n;
	int k;

	str[i][j] = '0';

	k = 1;
	for (n = 0; n < 2; n++){
		if (str[i + k][j] == '1' && i + k >= 0 && i + k < 12){
			is(i + k, j);
		}
		if (str[i][j + k] == '1' && j + k >= 0 && j + k < 12){
			is(i, j + k);
		}
		k = -1;
	}
}

