#include<cstdio>

using namespace std;

struct P{int x,y;};

P color[14][14];

P UF_root(P p)
{
	if(color[p.y][p.x].x==-1)	return p;
	return p=UF_root(color[p.y][p.x]);
}

bool UF_find(P p,P q)
{
	p=UF_root(p);
	q=UF_root(q);
	return p.x==q.x && p.y==q.y;
}

void UF_union(P p,P q)
{
	p=UF_root(p);
	q=UF_root(q);
	if(p.x!=q.x || p.y!=q.y)	color[q.y][q.x]=p;
}

int main()
{
	int world[14][14];
	for(int i=0;i<14;i++)for(int j=0;j<14;j++)world[i][j]=0;

	do{
		for(int i=1;i<13;i++){
			for(int j=1;j<13;j++)	world[i][j]=getchar()-'0';
			getchar();
		}
		for(int i=0;i<14;i++)
			for(int j=0;j<14;j++)
				color[i][j].x=-1;

		int dx[]={-1,0},dy[]={0,-1};
		for(int y=1;y<13;y++){
			for(int x=1;x<13;x++){
				for(int k=0;k<2;k++){
					P p={x,y},q={x+dx[k],y+dy[k]};
					if(world[p.y][p.x]==world[q.y][q.x] && !UF_find(p,q))
						UF_union(p,q);
				}
			}
		}

		int cnt=0;
		for(int y=1;y<13;y++)
			for(int x=1;x<13;x++)
				if(world[y][x]==1 && color[y][x].x==-1)	cnt++;
