void dfs(int x, int y) {
	field[y][x] = '0';
	for (int i = 0; i < 4; ++i) {
		int nx = x + dx[i];
		int ny = y + dy[i];
		if (0 <= nx&&nx < 12 && 0 <= ny&&ny < 12 && field[ny][nx] == '1') {
			dfs(nx, ny);
		}
	}
}

int solve() {
	int res = 0;
	for (int i = 0; i < 12; ++i) {
		for (int j = 0; j < 12; ++j) {
			if (field[i][j] == '1') {
				dfs(j, i);
				++res;
			}
		}
	}
	return res;
}

