void search(char map[14][14], int n, int m){

	if(map[n-1][m] == '1'){
		map[n-1][m] = '0';
		search(map, n-1, m);
	}
	if(map[n][m+1] == '1'){
		map[n][m+1] = '0';
		search(map, n, m+1);
	}
	if(map[n+1][m] == '1'){
		map[n+1][m] = '0';
		search(map, n+1, m);
	}
	if(map[n][m-1] == '1'){
		map[n][m-1] = '0';
		search(map, n, m-1);
	}

}

