bool flag[20][20]={false};
char mass[20][20]={'0'};
int cnt=0;

bool solve(int y,int x)
{
	if(flag[y][x]) return false;
	else flag[y][x]=true;

	if(mass[y][x-1]=='1' && !flag[y][x-1]) solve(y,x-1);
	if(!flag[y][x+1] && mass[y][x+1]=='1') solve(y,x+1);
	if(!flag[y+1][x] && mass[y+1][x]=='1') solve(y+1,x);

	return true;
}

