				if(mass[i][j]=='1' && solve(i,j)) cnt++;
			}
		}

		for(int i=0;i<20;i++){
			for(int j=0;j<20;j++){
				flag[i][j]=false;
			}
		}

		cout<<cnt<<endl;

		cnt=0;
	}
	return 0;
}

