		a = Q.front() / 100;
		b = Q.front() % 100;
		if (z[a + 1][b] == 1 && x[a + 1][b] == 0) {
			Q.push(Q.front() + 100);
			x[a + 1][b] = col;
		}
		if (z[a - 1][b] == 1 && x[a - 1][b] == 0) {
			Q.push(Q.front() - 100);
			x[a - 1][b] = col;
		}
		if (z[a][b + 1] == 1 && x[a][b + 1] == 0) {
			Q.push(Q.front() + 1);
			x[a][b + 1] = col;
		}
		if (z[a][b - 1] == 1 && x[a][b - 1] == 0) {
			Q.push(Q.front() + 1);
			x[a][b - 1] = col;
		}
		Q.pop();
	}
}

