void dfs(int x, int y)
{
	data[x][y] = '0';

	if (data[x - 1][y] == '1' && 0 <= (x - 1)){
		dfs(x - 1, y);
	}
	if (data[x + 1][y] == '1'){
		dfs(x + 1, y);
	}
	if (data[x][y - 1] == '1' && 0 <= (y - 1)){
		dfs(x, y - 1);
	}
	if (data[x][y + 1] == '1'){
		dfs(x, y + 1);
	}

	return;
}

