template <typename T>
T gcd(T x, T y) {
   if (x == 0 || y == 0) return 0;
   T r;
   while ((r = x % y) != 0) {
      x = y;
      y = r;
   }
   return y;
}

template <typename T>
T lcm(T x, T y) {
   if (x == 0 || y == 0) return 0;
   return (x / gcd(x, y) * y);
}

int next_combination(int sub)
{
   int x = sub & -sub, y = sub + x;
   return (((sub & ~y) / x) >> 1) | y;
}

std::uint64_t combination(int n, int r) {
   if (n < 0 || r < 0) return 0;
   if (n < r) return 0;
   std::uint64_t k = 1;
   for (std::uint64_t d = 1; d <= r; ++d) {
      k *= n--;
      k /= d;
   }
   return k;
}

