void drow(int x, int y)
{
	island[x][y] = 0;

	if(island[x - 1][y] == 1 && x - 1 >= 0)
	{
		drow((x - 1), y);
	}

	if(island[x + 1][y] == 1 && x + 1 <= 11)
	{
		drow((x + 1), y);
	}

	if(island[x][y - 1] == 1 && y - 1 >= 0)
	{
		drow( x, (y - 1));
	}

	if(island[x][y + 1] == 1 && y + 1 <=11)
	{
		drow( x, (y + 1));
	}
}

