void SagasiteHappy(int i, int j, int ary[12][12])
{
	ary[i][j] = 0;
	if (i > 0)
	{
		if (ary[i - 1][j] == 1)
		{
			SagasiteHappy(i - 1, j, ary);
		}
	}

	if (i < 12)
	{
		if (ary[i + 1][j] == 1)
		{
			SagasiteHappy(i + 1, j, ary);
		}
	}

	if (j > 0)
	{
		if (ary[i][j - 1] == 1)
		{
			SagasiteHappy(i, j - 1, ary);
		}
	}

	if (j < 12)
	{
		if (ary[i][j + 1] == 1)
		{
			SagasiteHappy(i, j + 1, ary);
		}
	}
}

