{
	int k = 0;
	while (k < 20) {
		int count = 0;
		char c;
		for (int i = 0; i < 12 * 12 && cin >> c; i++) {
			map[i] = c - '0';
		}
		for (int i = 0; i < 12; i++) {
			for (int j = 0; j < 12; j++) {
				if (map[i * 12 + j] == 1) {
					l.push_front(i * 12 + j);
					loop(i, j);
					count++;
				}
			}
		}
		cout << count << endl;
		k++;
	}
}

