void kensa(){

	PP S;
	queue<PP> P;
	int  w,h;
	int d=0,i=0,j=0,l=-1;
	int x[4]={ 1, 0,-1,0};
	int y[4]={ 0 ,1,0,-1};
	for(i=0;i<12;i++){
		for(j=0;j<12;j++){
		if(D[i][j]==1&&A[i][j]==0){
			S.x=j;
			S.y=i;
			P.push(S);
	while(1){
		l++;

		if(l==4) {
			l=0;
			P.pop();
			if(P.empty()){
				c++;
				l=-1;
			break;
			}
		}
		S=P.front();
		A[S.y][S.x]=c;
		S.x+=x[l];
		S.y+=y[l];
		if(S.y==12||S.x==12||S.x==-1||S.y==-1){
		continue;
		}
		if(A[S.y][S.x]==c||D[S.y][S.x]==0){
			continue;

		} 
		P.push(S);
	}
	}
	}
	}
}

