void dfs(int w, int h, int x, int y, std::vector<std::string> &image){
	image[y][x] = '0';

	if(x >= 1 && image[y][x - 1] == '1'){
		dfs(w, h, x - 1, y, image);
	}
	if(x < w - 1 && image[y][x + 1] == '1'){
		dfs(w, h, x + 1, y, image);
	}
	if(y < h - 1 && image[y + 1][x] == '1'){
		dfs(w, h, x, y + 1, image);
	}

	return;
}

