typedef pair<int, int> P;

int data[12][12] = {};
int dx[4] = {1, 0, -1, 0}, dy[4] = {0, 1 ,0 ,-1};

void dfs(int sx, int sy) {
  queue<P> que;
  data[sx][sy] = 2;
  que.push(P(sx, sy));

  while(que.size()) {
    P p = que.front();
    que.pop();

    for(int i = 0; i < 4; i++) {
