void bfs(int sy,int sx){
	int dx[] = {0,1,0,-1};
	int dy[] = {-1,0,1,0};
	queue<int> Qx;
	queue<int> Qy;
	Qx.push(sx);
	Qy.push(sy);
	int xn;
	int yn;
	int x_next;
	int y_next;
	Field[sy][sx] = false;
	while(!(Qx.empty() && Qy.empty())){
		xn = Qx.front();Qx.pop();
		yn = Qy.front();Qy.pop();
		//cout << xn << " " << yn << endl;
		for(int i=0;i<4;i++){
			x_next = xn + dx[i];
			y_next = yn + dy[i];
