int dx[] = {-1, 1, 0, 0};
int dy[] = {0, 0, -1, 1};

void solve(vector<vector<int>> f) {
  int r = 0;
  rep (i, 12) {
    rep (j, 12) {
      if (f[i][j] == 0) continue;
      ++r;
      queue<pair<int, int>> q;
      q.push({i,j});
      while (not q.empty()) {
        auto p = q.front(); q.pop();
        if (f[p.first][p.second] == 0) continue;
        f[p.first][p.second] = 0;
        rep (k, 4) {
          int i2 = p.first + dx[k],
              j2 = p.second + dy[k];
          if (i2 < 0 or j2 < 0 or i2 >= 12 or j2 >= 12) continue;
          if (f[i2][j2] == 0) continue;
          q.push({i2, j2});
        }
      }
    }
  }
  cout << r << endl;
}

