void acsess(int x,int y,int island[14][14]){
  if(island[x][y] != 1){
    return;
  }else{
    island[x][y] = 2;
  }

  acsess(x-1,y,island);
  acsess(x,y-1,island);
  acsess(x+1,y,island);
  acsess(x,y+1,island);
}

