#include <stdio.h>

#define WIDTH 12
#define HEIGHT 12

void deleteIsland( int, int );

int field[ WIDTH ][ HEIGHT ];
const int dx[ 4 ] = { -1, 0, 1, 0 }, dy[ 4 ] = { 0, -1, 0, 1 };

int main( void ) {
	int x, y, island;

	do {
		for ( y = 0; y < HEIGHT; y++ ) {
			for ( x = 0; x < WIDTH; x++ )
				scanf( "%c", &field[ x ][ y ] );
			scanf( "%*c" );
		}

		island = 0;
		for ( x = 0; x < WIDTH; x++ )
			for ( y = 0; y < HEIGHT; y++ )
				if ( field[ x ][ y ] == '1' ) {
					deleteIsland( x, y );

					island++;
				}

		printf( "%d\n", island );
	} while ( scanf( "%*c" ) != EOF );

	return 0;
}

void deleteIsland( int x, int y ) {
	int i;

	field[ x ][ y ] = '0';

	for ( i = 0; i < 4; i++ ) {
		int nx = x + dx[ i ], ny = y + dy[ i ];

		if ( nx >= 0 && nx < WIDTH && ny >= 0 && ny < HEIGHT && field[ nx ][ ny ] == '1' )
			deleteIsland( nx, ny );			
	}
}

