#include <stdio.h>

#define HEIGHT 12
#define WIDTH 12

enum { YET, ALREADY };

int calcIsland( void );
void findIsland( int, int );
void findIsland_body( int, int, int );

typedef struct {
	char type;
	int state;
} FIELD;

FIELD field[ HEIGHT ][ WIDTH ];
const int dx[ 4 ] = { -1, 0, 1, 0 };
const int dy[ 4 ] = { 0, -1, 0, 1 };

int main( void ) {
	int i, j;

	do {
		for ( i = 0; i < HEIGHT; i++ ) {
			for ( j = 0; j < WIDTH; j++ ) {
				scanf( "%c", &field[ i ][ j ].type );
				field[ i ][ j ].state = YET;
			}

			scanf( "%*c" );
		}

		printf( "%d\n", calcIsland() );
	} while ( scanf( "%*c" ) != EOF );

	return 0;
}

int calcIsland( void ) {
	int i, j, tmp;

	tmp = 0;
	for ( i = 0; i < HEIGHT; i++ )
		for ( j = 0; j < WIDTH; j++ )
			if ( field[ i ][ j ].type == '1' && field[ i ][ j ].state == YET ) {
				findIsland( i, j );
				tmp++;
			}

	return tmp;
}

void findIsland( int i, int j ) {
	field[ i ][ j ].state = ALREADY;

	findIsland_body( i, j + 1, 0 );
	findIsland_body( i + 1, j, 1 );
}

void findIsland_body( int i, int j, int comeDir ) {
	int d;

	if ( i < 0 || i >= HEIGHT || j < 0 || j >= WIDTH || field[ i ][ j ].state == ALREADY || field[ i ][ j ].type == '0' )
		return;

	field[ i ][ j ].state = ALREADY;

	for ( d = 0; d < 4; d++ )
		if ( d != comeDir )
			findIsland_body( i + dy[ d ], j + dx[ d ], d + 2 & 3 );
}

