#include <stdio.h>

void func(int **a, int i, int j)
{
	if(a[i][j] == 0){
		return ;
	}
	a[i][j] = 0;

	if(i-1 >= 0){
		func(a, i-1, j);
	}
	if(i+1 < 12){
		func(a, i+1, j);
	}
	if(j-1 >= 0){
		func(a, i, j-1);
	}
	if(j+1 < 12){
		func(a, i, j+1);
	}

	return ;
}

int main(int argc, char **argv)
{
	int i, j, **a, t;

	while(scanf("%1d", &t) != EOF){
		a = (int **)calloc(12, sizeof(int));
		for(i = 0; i < 12; i++){
			a[i] = (int *)calloc(12, sizeof(int));
		}
		a[0][0] = t;
		for(i = 1; i < 12; i++){
			scanf("%1d", &a[0][i]);
		}
		for(i = 1; i < 12; i++){
			for(j = 0; j < 12; j++){
				scanf("%1d", &a[i][j]);
			}
		}

		for(i = 0, t = 0; i < 12; i++){
			for(j = 0; j < 12; j++){
				if(a[i][j] == 1){
					func(a, i, j);
					t++;
				}
			}
		}
		printf("%d\n", t);

		for(i = 0; i < 12; i++){
			free(a[i]);
		}
		free(a);
	}

	return 0;
}

