					}
					if (i < 12 - 1 && island[j][i + 1] == '1') {		//一番下以外なら下側を調べる
						island[j][i + 1] = '2';							//マーキング
					}
					flag = 1;
				}
				if (island[j][i] == '1' || island[j][i] == '2') {		//常に処理
					if (j < 12 - 1 && island[j + 1][i] == '2') {		//一番右以外なら右側を調べる
						island[j][i] = '2';								//マーキング
					}
					if (j > 0 && island[j - 1][i] == '1') {				//一番左以外なら左側を調べる
						if (island[j][i] == '2') { island[j - 1][i] = '2'; }	//マーキング
					}
					if (j < 12 - 1 && island[j + 1][i] == '1') {		//一番右以外なら右側を調べる
						if (island[j][i] == '2') { island[j + 1][i] = '2'; }	//マーキング
					}
					if (i < 12 - 1 && island[j][i + 1] == '1') {		//一番下以外なら下側を調べる
						if (island[j][i] == '2') { island[j][i + 1] = '2'; }	//マーキング
					}
				}
				if (i > 0 && island[j][i - 1] == '2') {					//一番上下以外なら上側を調べる
					island[j][i - 1] = '0';								//消去
				}
			}
		}
		value += flag;
	} while (flag != 0);
	return value;
}

