#include <stdio.h>
#include <ctype.h>

#define N 12

int ld (
  int     * d,
  size_t    n
  )
{
  while ( n-- )
  {
    int ch = ' ';

    while ( ch != EOF && !isdigit ( ch ) )  ch = getchar ( );
    if ( ch == EOF )  return ( 0 );
    *( d++ ) = ch - '0';
  }

  return ( 1 );
}

int fill (
  int d[ N ][ N ],
  int x,
  int y
  )
{
  if ( x < 0 || y < 0
    || x >= N || y >= N ) return ( 0 );

  if ( d[ y ][ x ] )
  {
    d[ y ][ x ] = 0;
    fill ( d, x - 1, y );
    fill ( d, x + 1, y );
    fill ( d, x, y - 1 );
    fill ( d, x, y + 1 );
    return ( 1 );
  }

  return ( 0 );
}

/** Application main entry point. */
int main (
  int     argc,
  char  * argv[ ]
  )
{
  int map[ N ][ N ];
  int i, j;

  while ( ld ( (int *)map, N * N ) )
  {
    int res = 0;

    for ( j = 0; j < N; ++j )
    for ( i = 0; i < N; ++i )
      res += fill ( map, j, i );

    printf ( "%d\n", res );
  }

  return ( 0 );
}

