int chx[4] = { 1,0,-1,0 };												//4方向チェック用
int chy[4] = { 0,1,0,-1 };												//4方向チェック用

void format(int x, int y);
void hcheck(int x, int y);
int check(void);

int main(void) {
	int i, j, k;
	for (k = 0; k < 20; k++) {
		format(12, 12);													//配列の初期化
		for (i = 0; i < 12; i++) {
			for (j = 0; j < 13; j++) {
				map[j][i] = getchar();									//配列へ格納
//				if (map[0][0] == '\n') {
//					return 0;
//				}
			}
		}
		getchar();
		printf("%d\n", check());
	}
	return 0;
}
int check(void) {
	int i, j, flag = 0, value = 0;
	do {
		flag = 0;
		for (i = 0; i < 12; i++) {										//縦軸
			for (j = 0; j < 12; j++) {									//横軸
				if (map[j][i] == '1') {									//初回のみ処理
					hcheck(j, i);										//マーキング
					value++;
					flag = 1;
				}
			}
		}
	} while (flag != 0);												//処理できなくなるまで繰り返す
	return value;
}
void hcheck(int x, int y) {
	int i, px, py;
	map[x][y] = '0';
	for (i = 0; i < 4; i++) {
		px = x + chx[i], py = y + chy[i];
		if (px < 0 || px > 11) { continue; }							//配列の外は見ない
		if (py < 0 || py > 11) { continue; }							//配列の外は見ない
		if (map[px][py] == '1') { hcheck(px, py); }						//再帰呼び出し
	}
}
void format(int x, int y) {
	int i, j;
	for (i = 0; i < x; i++) {
		for(j = 0; j < y; j++){
			map[j][i] = '0';
		}
	}
}

#include <stdio.h>
#define N 12
char b[N][128];
int dx[]={ 1, -1, 0, 0 };
int dy[]={ 0, 0, -1, 1 };
void dfs(int y, int x)
{
    int r, c, k;
    b[y][x]='0';
    for (k=0; k<4; ++k) {
        r=y+dy[k];
        c=x+dx[k];
        if (r<0 || r>=N || c<0 || c>=N || b[r][c]=='0') continue;
        dfs(r, c);
    }
}
int main(void)
{
    while (1) {
        int i, j, res=0;
        for (i=0; i<N; ++i) fgets(b[i], 128, stdin);
        for (i=0; i<N; ++i) for (j=0; j<N; ++j)
            if (b[i][j]=='1') { res++; dfs(i, j); }
        printf("%d\n", res);
        if (!fgets(b[0], 128, stdin)) break;
    }
    return 0;
}

#include <stdio.h>
#include <stdlib.h>

void fillField(char *s,int line,int pos){
	if(line<0||line>=12||pos<0||pos>=12) return;
	if(*(s+line*13+pos)!='1') return;
	*(s+line*13+pos)='2';
	fillField(s,line+1,pos);
	fillField(s,line-1,pos);
	fillField(s,line,pos+1);
	fillField(s,line,pos-1);
}

int main(void){
	char *field = malloc(12*13*sizeof(char));
	do{
		int i,j;
		for(i=0;i<12;++i){
			scanf("%s%*c",field+13*i);
		}
		int islands = 0;
		for(i=0;i<12;++i){
			for(j=0;j<12;++j){
				if(*(field+13*i+j)=='1'){
					++islands;
					fillField(field,i,j);
				}
			}
		}
		printf("%d\n",islands);
	}while(scanf("%*c")!=EOF);
	free(field);
	return 0;
}

/* 1168ツづ個パツーツスツ閉板閉ェツつェツづ卍づ堋つサツづ個づ慊づ慊使ツつヲツづゥ */

#include <stdio.h>
#define SIZE 12
#define Q_MAX 12*12

/* Queue */

int Q[Q_MAX], sp, rp;

void Qinit(){ sp=0, rp=0; }
void Qstore(int N, int M){ Q[sp++%Q_MAX]=N; Q[sp++%Q_MAX]=M; }
int Qretrive(){ return Q[rp++%Q_MAX]; }
int Qempty(){ return sp==rp; }

/* main */

int input[SIZE][SIZE], islands;

void analyzeIsland(int x, int y){	/* analyze an island */
	islands++, Qinit();

	input[x][y]=0; Qstore(x,y);
	while(!Qempty()){
		x=Qretrive(); y=Qretrive();
		if(x+1<SIZE && input[x+1][y]==1) input[x+1][y]=0, Qstore(x+1,y);
		if(y+1<SIZE && input[x][y+1]==1) input[x][y+1]=0, Qstore(x,y+1);
		if(x>0 && input[x-1][y]==1) input[x-1][y]=0, Qstore(x-1,y);
		if(y>0 && input[x][y-1]==1) input[x][y-1]=0, Qstore(x,y-1);
	}
}

int main(void){
	int x, y; char c;

	do{
		islands=0;

		for(y=0; y<SIZE; y++){
			for(x=0; x<SIZE; x++) input[x][y]=getchar()-'0';
			getchar();	/* ツ嘉シツ行ツづーツ偲個づづゥ */
		}

		for(y=0; y<SIZE; y++)
			for(x=0; x<SIZE; x++)
				if(input[x][y]==1) analyzeIsland(x,y);

		printf("%d\n",islands);
	}while(getchar()!=EOF);

	return 0;
}

