char *gets(char *);
char buf[100];

typedef struct { char r, c; } T;
char a[14][14];
char f[14][14];
T s[14 * 14];
int top;

void see(int r, int c, int n)
{
//	printf("See [%d,%d], n=%d\n", r, c, n);
	f[r][c] = n, s[0].r = r, s[0].c = c;
	for (top = 1; top; ) {
		r = s[--top].r, c = s[top].c;
		if (a[r - 1][c] == '1' && !f[r - 1][c]) f[r - 1][c] = n, s[top].r = r - 1, s[top++].c = c;
		if (a[r + 1][c] == '1' && !f[r + 1][c]) f[r + 1][c] = n, s[top].r = r + 1, s[top++].c = c;
		if (a[r][c - 1] == '1' && !f[r][c - 1]) f[r][c - 1] = n, s[top].r = r, s[top++].c = c - 1;
		if (a[r][c + 1] == '1' && !f[r][c + 1]) f[r][c + 1] = n, s[top].r = r, s[top++].c = c + 1;
	}
}

int main()
{
	int r, c, k = 1, cnt;

	while (1) {
		if (k) k = 0;
		else if (gets(buf) == NULL) break;
		for (r = 1; r <= 12; r++) gets(&a[r][1]);
		memset(f, 0, sizeof(f));
		for (cnt = 0, r = 1; r <= 12; r++) for (c = 1; c <= 12; c++) {
			if (a[r][c] == '1' && !f[r][c]) see(r, c, ++cnt);
		}
		printf("%d\n", cnt);
	}
	return 0;
}

