	int i;
	a[y][x]='0';
	for(i=0;i<4;i++){
		if(x+dx[i]>=0&&x+dx[i]<12&&a[y][x+dx[i]]=='1') func(y,x+dx[i]);
		if(y+dy[i]>=0&&y+dy[i]<12&&a[y+dy[i]][x]=='1') func(y+dy[i],x);
