#include <stdio.h>

int map[12][12],ans;
void dp(int,int);

int main(){
	int i,j;
	while(1){
		ans = 0;
		for(i = 0;i < 12;i++){
			for(j = 0;j < 12;j++){
				if(scanf("%1d",&map[i][j]) == EOF) return 0;
			}
		}
		for(i = 0;i < 12;i++){
			for(j = 0;j < 12;j++){
				if(map[i][j] == 1) {
					dp(i,j);
					ans++;
				}
			}
		}
		printf("%d\n",ans);
	}
	return 0;
}

void dp(int x,int y){
	int i,j;
	map[x][y] = 0;
	for(i = -1;i <= 1;i++){
		for(j = -1;j <= 1;j++){
			if((i + j == -1 || i + j == 1) && x + i >= 0 && x + i < 12 && y + j >= 0 && y + j < 12 && map[x + i][y + j] == 1) dp(x + i,y + j);
		}
	}
	return;
}

