void dfs(int* data,int* label,int index) {

  int new[4][2]={{1,0},{0,1},{-1,0},{0,-1}};

  int x,y;
  int nx,ny;

  int i;

  label[index]=1;

  x=index%12;
  y=index/12;

  for(i=0;i<4;i++) {

    nx=x+new[i][0];
    ny=y+new[i][1];

    if(nx<0 || nx>11 || ny<0 || ny>11) continue;

    if(label[nx+ny*12]==0 && data[nx+ny*12]==1) dfs(data,label,nx+ny*12);

  }

}

