#include <stdio.h>
#include <string.h>

#define DEBUG 0
#define BUFSIZE 20

#define true 1
#define false 0

int data[12][12];
int active;



int paint(int i, int j, int color){
    if(!(0 <= i && i<12 && 0<= j && j<12))
        return false;
    if(data[i][j]!=1){
        return false;
    }

    data[i][j] = color;

    paint(i+1, j, color);
    paint(i-1, j, color);
    paint(i, j+1, color);
    paint(i, j-1, color);
    return true;
}


void loadData(char* buf){
    int i = 0;
    while(*buf && i<12){
        data[active][i] = *buf - '0';
        i++;
        buf++;
    }
    if(DEBUG){
        for(i=0;i<12; i++){
            printf("%d", data[active][i]);
        }
        printf("\n");
    }
    active++;
}

void endData(void){
    int color;
    int i, j;
    active = 0;
    if(DEBUG){
        printf("\n");
    }

    color = 2;
    for(i=0;i<12;i++){
        for(j=0;j<12;j++){
            if(paint(i, j, color)){
                color++;
            }
        }
    }
    if(DEBUG){
        for(i=0;i<12;i++){
            for(j=0;j<12;j++){
                if(data[i][j]){
                    printf("%c", data[i][j] + 'a' - 2);
                }else{
                    printf(" ");
                }
            }
            printf("\n");
        }
    }
    printf("%d\n", color -2);
}


int main(){
    char buf[BUFSIZE];
    active = 0;

    while(fgets(buf, BUFSIZE, stdin)){
        if(strlen(buf) > 2){
            loadData(buf);
        }else{
            endData();
        }
    }
    if(active){
        endData();
    }
    return 0;
}

#include <stdio.h>

#define WIDTH 12
#define HEIGHT 12

void deleteIsland( int, int );

int field[ WIDTH ][ HEIGHT ];
const int dx[ 4 ] = { -1, 0, 1, 0 }, dy[ 4 ] = { 0, -1, 0, 1 };

int main( void ) {
	int x, y, island;

	do {
		for ( y = 0; y < HEIGHT; y++ ) {
			for ( x = 0; x < WIDTH; x++ )
				scanf( "%c", &field[ x ][ y ] );
			scanf( "%*c" );
		}

		island = 0;
		for ( x = 0; x < WIDTH; x++ )
			for ( y = 0; y < HEIGHT; y++ )
				if ( field[ x ][ y ] == '1' ) {
					deleteIsland( x, y );

					island++;
				}

		printf( "%d\n", island );
	} while ( scanf( "%*c" ) != EOF );

	return 0;
}

void deleteIsland( int x, int y ) {
	int i;

	field[ x ][ y ] = '0';

	for ( i = 0; i < 4; i++ ) {
		int nx = x + dx[ i ], ny = y + dy[ i ];

		if ( nx >= 0 && nx < WIDTH && ny >= 0 && ny < HEIGHT && field[ nx ][ ny ] == '1' )
			deleteIsland( nx, ny );
	}
}

