	while (scanf("%s", island[0]) != EOF){		
		for (i = 1; i < 12; i++){
			scanf("%s", island[i]);
		}

		for (i = 0; i < 12; i++){
			for (j = 0; j < 12; j++){
				island_number[i][j] = i * 12 + j + 1;
				num[i * 12 + j] = 0;
			}
		}

		for (i = 0; i < 12; i++){
			for (j = 0; j < 12; j++){
				if (island[i][j] == '1'){
					DFS(i, j);
				}
				else {
					island_number[i][j] = 0;
				}
			}
		}

		for (i = 0; i < 12; i++){
			for (j = 0; j < 12; j++){
				if (island_number[i][j] > 0){
					num[island_number[i][j] - 1] = 1;
				}
			}
		}

		for (i = 0, cnt = 0; i < 12 * 12; i++){
			if (num[i] == 1){
				cnt++;
			}
		}

		printf("%d\n", cnt);
	}

	return (0);
}

