void search(int i,int j){
    w[i][j]=0;
    if(i!=MAX-1)if(w[i+1][j])search(i+1,j);
    if(i!=0)if(w[i-1][j])search(i-1,j);
    if(j!=MAX-1)if(w[i][j+1])search(i,j+1);
    if(j!=0)if(w[i][j-1])search(i,j-1);
}

