#include <stdio.h>

const int Field_Size = 12;
const int xdir[4] = {0, -1, 0, 1};
const int ydir[4] = {1, 0, -1, 0};

void dfs_algorithm(char field[Field_Size][Field_Size+2], int cur_x, int cur_y)
{
  int i, next_x, next_y;

  field[cur_y][cur_x] = '0';
  for(i = 0; i < sizeof xdir/ sizeof xdir[0]; ++i){
    next_x = cur_x + xdir[i];
    next_y = cur_y + ydir[i];
    if(0 <= next_x && next_x < Field_Size &&
       0 <= cur_y && cur_y < Field_Size && field[next_y][next_x] == '1')
      dfs_algorithm(field, next_x, next_y);
  }
}

int main()
{
  int i, j, count;
  char field[Field_Size][Field_Size+2];

  do{
    for(i = 0; i < Field_Size; ++i)
      fgets(field[i], sizeof field[i]/sizeof field[i][0], stdin);

    count = 0;
    for(i = 0; i < Field_Size; ++i)
      for(j = 0; j < Field_Size; ++j)
	if(field[i][j] == '1'){
	  dfs_algorithm(field, j, i);
	  ++count;
	}

    printf("%d\n", count);
  } while(fgets(field[0], sizeof field/sizeof field[0][0], stdin)!=NULL);

  return 0;
}

