void spread(int masu[12][12], int x, int y) {                                   
    masu[y][x] = 0;                                                             
    if (x > 0 && masu[y][x-1] == 1) {                                           
        spread(masu, x-1, y);                                                   
    }                                                                           
    if (x < 11 && masu[y][x+1] == 1) {                                          
        spread(masu, x+1, y);                                                   
    }                                                                           
    if (y > 0 && masu[y-1][x] == 1) {                                           
        spread(masu, x, y-1);                                                   
    }                                                                           
    if (y < 11 && masu[y+1][x] == 1) {                                          
        spread(masu, x, y+1);                                                   
    }                                                                           
}  

int calc(int masu[12][12]) {                                                    
    int num = 0;                                                                
    int x, y;                                                                   

    for (x = 0; x < 12; x++) {                                                  
        for (y = 0; y < 12; y++) {                                              
            if (masu[y][x] == 1) {                                              
                spread(masu, x, y);                                             
                num++;                                                          
            }                                                                   
        }                                                                       
    }                                                                           
    return num;                                                                 
}  

