#define N 12
char b[N][N+2];
int dx[]={ 1, -1, 0, 0 };
int dy[]={ 0, 0, -1, 1 };
void dfs(int i, int j)
{
    int r, c, k;
    for (k=0; k<4; ++k) {
        r=i+dy[k];
        c=j+dx[k];
        if (r<0 || r>=N || c<0 || c>=N || b[r][c]=='0') continue;
        b[r][c]='0';
        dfs(r, c);
    }
}
int main(void)
{
    while (1) {
        int i, j, res=0;
        for (i=0; i<N; ++i) fgets(b[i], N+2, stdin);
        for (i=0; i<N; ++i) for (j=0; j<N; ++j)
            if (b[i][j]=='1') { res++; b[i][j]='0'; dfs(i, j); }
        printf("%d\n", res);
        if (!fgets(b[0], N+1, stdin)) break;
    }
    return 0;
}

