#include <stdio.h>
#include <string.h>

int map[14][14];


void init_map(void)
{
	memset(map, 0, sizeof(map));
}

void input_map(void)
{
	char line[20];
	int x, y;

	for (y = 1; y <= 12; y++){
		fgets(line, 20, stdin);
		if (strlen(line) < 10){
			exit(0);
		}
		for (x = 1; x <= 12; x++){
			map[y][x] = (line[x - 1] - '0') * -1;
		}
	}
}

int find_island(int island)
{
	int x, y;

	for (y = 1; y <= 12; y++){
		for (x = 1; x <= 12; x++){
			if (map[y][x] == -1){
				 map[y][x] = island;
				 return (1);
			}
		}
	}
	return (0);
}

void mark_island(int island)
{
	int flag;
	int x, y;

	flag = 1;
	while (flag == 1){
		flag = 0;	
		for (y = 1; y <= 12; y++){
			for (x = 1; x <= 12; x++){
				if (map[y][x] == -1){
					if (map[y    ][x - 1] == island ||
						map[y - 1][x    ] == island ||
						map[y    ][x + 1] == island ||
						map[y + 1][x    ] == island){
						map[y][x] = island;
						flag = 1;
					}
				}
			}
		}
	}

#if 0
	printf("-------------\n");
	for (y = 1; y <= 12; y++){
		for (x = 1; x <= 12; x++){
			printf("[%2d]", map[y][x]);
		}
		printf("\n");
	}
#endif

}


int main(void)
{
	char dummy[100];
	int island;

	while (1){
		island = 1;
		init_map();

		input_map();

		while (1){
			if (find_island(island) == 0){
				break;
			}
			mark_island(island);
			island++;
		}

		printf("%d\n", island - 1);

		if (fgets(dummy, 20, stdin) == NULL){
			break;
		}
	}

	return (0);
}

