void search(int i,int j,int k)
{
  if(n[i-1][j] == 1 && m[i-1][j] == 0) {
    m[i-1][j] = k;
    search(i-1,j,k);
  }
  if(n[i+1][j] == 1 && m[i+1][j] == 0) {
    m[i+1][j] = k;
    search(i+1,j,k);
  }
  if(n[i][j-1] == 1 && m[i][j-1] == 0) {
    m[i][j-1] = k;
    search(i,j-1,k);
  }
  if(n[i][j+1] == 1 && m[i][j+1] == 0) {
    m[i][j+1] = k;
    search(i,j+1,k);
  }
}
int main()
{
  int i,j,k;
  char c;
  for(;;){
    memset(n,0,sizeof(n));
    memset(m,0,sizeof(m));
    for(i=1;i<=12;i++){
      for(j=1;j<=12;j++){
	if((scanf("%c",&c)) == EOF) return 0;
	n[i][j] = c-'0';
      }
      scanf("\n");
    }
    k = 1;
    for(i=1;i<=12;i++){
      for(j=1;j<=12;j++){
	if(n[i][j] == 1 && m[i][j] == 0) {
	  m[i][j] = k;
	  search(i,j,k++);
	}
      }
    }
    printf("%d\n", k-1);
    if((scanf("\n")) == EOF) return 0;
  }
}

