#include <stdio.h>
#define N 12

void paint( char map[ N ][ N ], int x, int y ) {
  if( x < 0 || y < 0 || N <= x || N <= y )
    return ;
  if( map[ x ][ y ] != '1' )
    return ;

  map[ x ][ y ] = '0';

  paint( map, x + 1, y );
  paint( map, x - 1, y );
  paint( map, x, y + 1 );
  paint( map, x, y - 1 );
}

int main( void ) {

  while( 1 ) {
    int i, j;
    char map[ N ][ N ], hoge;
    int count;

    for( i = 0; i < N; i++ ) {
      for( j = 0; j < N; j++ )
        scanf( "%c", &map[ i ][ j ] ); // マップ読み取り
      scanf( "%c", &hoge ); // 改行飛ばし
    }
    if( scanf( "%c", &hoge ) != 1 ) // 空白の一行飛ばし
      break;

    count = 0;
    for( i = 0; i < N; i++ )
      for( j = 0; j < N; j++ )
        if( map[ i ][ j ] == '1' ) {
          count++;
          paint( map, i, j );
        }

    printf( "%d\n", count );
  } 

  return ( 0 );
}

