						map[j][i] = '2';								//マーキング
					}
					if (i > 0 && map[j][i - 1] == '1') {				//一番上以外なら上側を調べる
						if (map[j][i] == '2') { map[j][i - 1] = '2'; }	//マーキング
					}
					if (j > 0 && map[j - 1][i] == '1') {				//一番左以外なら左側を調べる
						if (map[j][i] == '2') { map[j - 1][i] = '2'; }	//マーキング
					}
					if (j < 12 - 1 && map[j + 1][i] == '1') {			//一番右以外なら右側を調べる
