int check(void) {
	int i, j, flag = 0, value = 0;
	do {
		flag = 0;
		for (i = 0; i < 12; i++) {										//縦軸
			for (j = 0; j < 12; j++) {									//横軸
				if (flag == 0 && map[j][i] == '1') {					//初回のみ処理
					map[j][i] = '2';									//マーキング
					if (j < 12 - 1 && map[j + 1][i] == '1') {			//一番右以外なら右側を調べる
						map[j + 1][i] = '2';							//マーキング
					}
					if (i < 12 - 1 && map[j][i + 1] == '1') {			//一番下以外なら下側を調べる
						map[j][i + 1] = '2';							//マーキング
					}
					flag = 1;
				}
				if (map[j][i] == '1' || map[j][i] == '2') {				//常に処理(左から右)
					if (j < 12 - 1 && map[j + 1][i] == '2') {			//一番右以外なら右側を調べる
						map[j][i] = '2';								//マーキング
					}
					if (j > 0 && map[j - 1][i] == '1') {				//一番左以外なら左側を調べる
						if (map[j][i] == '2') { map[j - 1][i] = '2'; }	//マーキング
					}
					if (j < 12 - 1 && map[j + 1][i] == '1') {			//一番右以外なら右側を調べる
						if (map[j][i] == '2') { map[j + 1][i] = '2'; }	//マーキング
					}
					if (i < 12 - 1 && map[j][i + 1] == '1') {			//一番下以外なら下側を調べる
						if (map[j][i] == '2') { map[j][i + 1] = '2'; }	//マーキング
					}
				}

				if (map[11 - j][i] == '1' || map[11 - j][i] == '2') {				//常に処理(右から左)
					if (11 - j < 12 - 1 && map[11 - j + 1][i] == '2') {				//一番右以外なら右側を調べる
						map[11 - j][i] = '2';										//マーキング
					}
					if (11 - j > 0 && map[11 - j - 1][i] == '1') {					//一番左以外なら左側を調べる
						if (map[11 - j][i] == '2') { map[11 - j - 1][i] = '2'; }	//マーキング
					}
					if (11 - j < 12 - 1 && map[11 - j + 1][i] == '1') {				//一番右以外なら右側を調べる
						if (map[11 - j][i] == '2') { map[11 - j + 1][i] = '2'; }	//マーキング
					}
					if (11 - i < 12 - 1 && map[11 - j][i + 1] == '1') {				//一番下以外なら下側を調べる
						if (map[11 - j][i] == '2') { map[11 - j][i + 1] = '2'; }	//マーキング
					}
				}

				if (i > 0 && map[j][i - 1] == '2') {					//一番上下以外なら上側を調べる
					map[j][i - 1] = '0';								//消去
				}
			}
		}
		value += flag;													//処理できた場合は島の数に加算
	} while (flag != 0);												//処理できなくなるまで繰り返す
	return value;
}

