	if(table[i][j])
		return;

	if(input[i][j] == '1')
		table[i][j] = 1;
	else
		return;

	if(i != 0)
		check(i - 1, j);
	if(i != size - 1)
		check(i + 1, j);
	if(j != 0)
		check(i, j - 1);
	if(j != size - 1)
		check(i, j + 1);

	return;
}

